import React from 'react'
import s from './Dialogs.module.css'
import {NavLink} from "react-router-dom";


const DialogItem = (props) => {
    return (
        <NavLink to={'/dialogs/' + props.id} activeClassName={s.active}>
            {props.name}
        </NavLink>
    )
};


const Message = (props) => {
    return (
        <div className={s.message}>{props.message}</div>
    )
};


const Dialogs = (props) => {

    let dialogsData = [
        {id: '1', name: 'Nikolai'},
        {id: '2', name: 'Petr'},
        {id: '3', name: 'Alexandr'},
        {id: '4', name: 'Ana'},
        {id: '5', name: 'Sergei'},
        {id: '6', name: 'Julia'},
    ];


    let messageData = [
        {id: '1', text: 'Hi'},
        {id: '2', text: 'How are you'},
        {id: '3', text: 'I`m fine'},
        {id: '4', text: 'Ana'},
        {id: '5', text: 'Sergei'},
        {id: '6', text: 'Julia'},
    ];

    let dialogsElement = dialogsData.map((dialog) => {
            return (
                <DialogItem name={dialogsData.name} id={dialogsData.id}/>
            )
        }
    );


    let dialogsElement1 = dialogsData.map( () => {

            if(isMale(dialogsData.name)) {
                return 1;
            }

            else {
                    return 0;
                
            }

        }

    );

    return (
        <div className={s.dialogs}>

            <div className={s.dialogItem}>
                Dialog

                <DialogItem name={dialogsData[0].name} id={dialogsData[0].id}/>
                <DialogItem name={dialogsData[1].name} id={dialogsData[1].id}/>
                <DialogItem name={dialogsData[2].name} id={dialogsData[2].id}/>
                <DialogItem name={dialogsData[3].name} id={dialogsData[3].id}/>
                <DialogItem name={dialogsData[4].name} id={dialogsData[4].id}/>
                <DialogItem name={dialogsData[5].name} id={dialogsData[5].id}/>


            </div>

            <div className={s.messages}>

                <Message message={messageData[0].text}/>
                <Message message={messageData[1].text}/>
                <Message message={messageData[2].text}/>


            </div>
        </div>
    )
};

export default Dialogs
