import React from 'react'
import s from './Dialogs.module.css'
import {NavLink} from "react-router-dom";


const DialogItem = (props) => {
    return (
        <NavLink to={'/dialogs/' + props.id} activeClassName={s.active}>
            {props.name}
        </NavLink>
    )
};


const Message = (props) => {
    return (
        <div className={s.message}>{props.message}</div>
    )
};


let Dialogs;
Dialogs = (props) => {

    let dialogsData = [
        {id: '1', name: 'Nikolai'},
        {id: '2', name: 'Petr'},
        {id: '3', name: 'Alexandr'},
        {id: '4', name: 'Ana'},
        {id: '5', name: 'Sergei'},
        {id: '6', name: 'Julia'},
    ];


    let messageData = [
        {id: '1', text: 'Hi'},
        {id: '2', text: 'How are you'},
        {id: '3', text: 'I`m fine'},
        {id: '4', text: 'Ana'},
        {id: '5', text: 'Sergei'},
        {id: '6', text: 'Julia'},
    ];

    let dialogsElement = dialogsData

        .map((dialog) => {
                return (
                    <DialogItem name={dialogsData.name} id={dialogsData.id}/>
                )
            }
        );


    let messageElement = messageData.map((message) => {
            return (
                <Message message={messageData[1].text}/>
            )
        }
    );


    return (
        <div className={s.dialogs}>

            <div className={s.dialogItem}>
                Dialog


            </div>

            <div className={s.messages}>

                


            </div>
        </div>
    )
};

export default Dialogs
